using ProgressSoft.Core.Entites; // Ensure this is necessary for your context
using ProgressSoft.Core.Helper.FileUpload.Reader;
using System.Drawing.Imaging;
using System.IO;
using System.Runtime.InteropServices;
using System.Text.Json;
using ZXing;
using ZXing.Windows.Compatibility;


GenerateQrCode();
GenerateCsvFile();
 static void GenerateQrCode()
{
    var barcodeWriter = new BarcodeWriter
    {
        Format = BarcodeFormat.QR_CODE,
        Options = new ZXing.Common.EncodingOptions
        {
            Width = 200,
            Height = 200
        }
    };

    // Combine the directory with the JSON file name
    string filePath = Path.Combine(@"C:\Users\ahmad\ProgressSoftTask\API\QRCodeGenrator", "CardReaders.json");

    // Read the JSON file content
    string jsonContent = File.ReadAllText(filePath);

    string desktopPath;

    // Check if running on Windows
    if (RuntimeInformation.IsOSPlatform(OSPlatform.Windows))
    {
        desktopPath = Environment.GetFolderPath(Environment.SpecialFolder.Desktop);
    }
    else
    {
        Console.WriteLine("This application is designed to run on Windows.");
        return;
    }

    string imagePath = Path.Combine(desktopPath, "barcode.png");

    var barcodeBitmap = barcodeWriter.Write(jsonContent);

    barcodeBitmap.Save(imagePath, ImageFormat.Png);

}


static void GenerateCsvFile()
{
    var cardReaders = new List<CardReader>()
    {
        new() {Name = "Ali Alawneh" , Address = "Irbid" , Email = "ali@gmail.com" , Gender = "Male", Phone ="07834343432" ,  DateOfBirth = new DateTime(2002, 6, 15),Photo = "" },
    };

    
     string desktopPath = Environment.GetFolderPath(Environment.SpecialFolder.Desktop);
    string filePath = Path.Combine(desktopPath, "CardReaders.csv");

    // Write data to the CSV file
    using (var writer = new StreamWriter(filePath))
    {
        // Write header
        writer.WriteLine("Name,Gender,DateOfBirth,Email,Phone,Address,Photo");

        // Write data
        foreach (var cardReader in cardReaders)
        {
            writer.WriteLine($"{cardReader.Name},{cardReader.Gender},{cardReader.DateOfBirth:yyyy-MM-dd},{cardReader.Email},{cardReader.Phone},{cardReader.Address},{cardReader.Photo}");
        }
    }
}
